import React from 'react';
import {useState} from "react";

const LogIn = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [data, setData] = useState(null);

    const handleUsernameChange = (event) => {
        setUsername(event.target.value);
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
    };

    const handleClick = async (event) => {
        event.preventDefault(); // Prevent the default form submission behavior

        try {
            const response = await fetch(`http://localhost:5000/login?username=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password })
            });

            if (response.ok) {
                const result = await response.json();
                setData(result);
                alert('Login succeeded');
            } else {
                alert('Login failed');
            }
        } catch (error) {
            alert('Error:' + error);
        }
    };

    return (
        <div>
            <form onSubmit={handleClick}>
                <label>Username:
                    <input type="text" id="username" name="username" value={username} onChange={handleUsernameChange}/>
                </label><br/>
                <label>Password:
                    <input type="password" id="username" name="username" value={password} onChange={handlePasswordChange}/>
                </label><br/>
                <button type="submit">Log-in</button>
            </form>
            {data && <div>Login successful! Welcome, {data.username}.</div>}
        </div>
    );
};

export default LogIn;
