import React from 'react';
import {useState} from "react";

const LogIn = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [data, setData] = useState(null);

    const handleUsernameChange = (event) => {
        setUsername(event.target.value);
    };

    const handlePasswordChange = (event) => {
        setPassword(event.target.value);
    };

    const handleClick = async (event) => {
        event.preventDefault(); // Prevent the default form submission behavior

        const xhr = new XMLHttpRequest();
        xhr.open('GET', `http://localhost:5000/login?username=${encodeURIComponent(username)}&password=${encodeURIComponent(password)}`, true);
        xhr.setRequestHeader('Content-Type', 'application/json');

        xhr.onload = function() {
            if (xhr.status >= 200 && xhr.status < 300) {
                const result = JSON.parse(xhr.responseText);
                setData(result);
                alert('Login succeeded');
            } else {
                alert('Login failed');
            }
        };

        xhr.onerror = function() {
            alert('Error occurred while making the request');
        };

        xhr.send();
    };

    return (
        <div>
            <form onSubmit={handleClick}>
                <label>Username:
                    <input type="text" id="username" name="username" value={username} onChange={handleUsernameChange}/>
                </label><br/>
                <label>Password:
                    <input type="password" id="username" name="username" value={password} onChange={handlePasswordChange}/>
                </label><br/>
                <button type="submit">Log-in</button>
            </form>
            {data && <div>Login successful! Welcome, {data.username}.</div>}
        </div>
    );
};

export default LogIn;
