import React, { useState } from 'react';
import { executeQuery } from '../database'; // Import the executeQuery function

const LogIn = () => {
    const [formData, setFormData] = useState({
        username: '',
        email: ''
    });

    // Update form data when input fields change
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Construct the INSERT query
            const query = ``;

            // Execute the INSERT query
            await executeQuery(query);

            // Clear the form data after successful insertion
            setFormData({ username: '', email: '' });

            // Optionally, you can show a success message or trigger some other action
            alert('Data inserted successfully!');
        } catch (error) {
            console.error("Error inserting data:", error);
            // Optionally, you can show an error message or handle the error in another way
            alert('An error occurred while inserting data. Please try again.');
        }
    };

    return (
        <div>
            <form>
                <label>Username:
                    <input type="text" id="username" name="username"/>
                </label><br/>
                <label>Password:
                    <input type="password" id="username" name="username"/>
                </label><br/>
                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default LogIn;
