import React, { useState } from 'react';
import { executeQuery } from '../database'; // Import the executeQuery function

function LogIn(){
    const [formData, setFormData] = useState({
        username: '',
        password: ''
    });

    // Update form data when input fields change
    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    // Handle form submission
    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            // Construct the INSERT query
            const query = `SELECT * from spotiforums_users
                                where user_username = '${formData.username}' and
                                user_password = '${formData.password}'`;

            // Execute the INSERT query
            const rows = await executeQuery(query);

            if (rows.length === 0) {
                alert("Invalid login");
            }

            // Clear the form data after successful insertion
            setFormData({ username: '', password: '' });

            // Optionally, you can show a success message or trigger some other action
            alert('Data inserted successfully!');
        } catch (error) {
            console.error("Error inserting data:", error);
            // Optionally, you can show an error message or handle the error in another way
            alert('An error occurred while inserting data. Please try again.');
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>Username:
                    <input type="text" id="username" value={formData.username} onChange={handleChange}/>
                </label><br/>
                <label>Password:
                    <input type="password" id="username" value={formData.password} onChange={handleChange}/>
                </label><br/>
                <button type="submit">Register</button>
            </form>
        </div>
    );
};

export default LogIn;
